settings.markerImageFunction:
var type = dsData[dsIndex]['type'];
if (type == 'smart-sensor') {
	var res = {
	    url: images[0],
	    size: 60
	}
	return res;
} else if (type == 'hvac') {
    var enabled = dsData[dsIndex]['enabled'];
	var res = {
	    url: enabled === 'true' ? images[2] : images[1],
	    size: 60
	}
	return res;
} else if (type == 'energy-meter') {
    var res = {
	    url: images[3],
	    size: 60
	}
	return res;
} else if (type == 'water-meter') {
    var res = {
	    url: images[4],
	    size: 60
	}
	return res;
}

settings.labelFunction:
var deviceType = dsData[dsIndex]['deviceType'];
if (typeof deviceType !== undefined) {
    if (deviceType == "energy meter") {
        return '<span style="color:orange;">${entityName}, ${energy:2} kWt</span>';
    } else if (deviceType == "thermometer") {
        return '<span style="color:blue;">${entityName}, ${temperature:2} °C</span>';
    }
}

settings.tooltipFunction:
var deviceType = dsData[dsIndex]['type'];

var tooltip = '<b>${friendlyName}</b><br/>${entityName}<br/>';
var typeTooltip = '';

if (deviceType == "smart-sensor") {
    typeTooltip = '<br/><span style="font-weight: 500; color:#666;">Temperature:</span>&nbsp;<span style="color:#f44336; font-weight: 600;">${temperature:1} °C</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">Humidity:</span>&nbsp;<span style="color:#1e88e5; font-weight: 600;">${humidity:1} %</span><br/>';
    typeTooltip += '<br/><b>Air quality</b><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">CO2 level:</span>&nbsp;<span style="color:#9f4295; font-weight: 600;">${co2:0} ppm</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">TVOC level:</span>&nbsp;<span style="color:#3d9d9a; font-weight: 600;">${tvoc:3} ppm</span><br/>';
    typeTooltip += '<br/><span style="font-weight: 500; color:#666;">Occupancy:</span>&nbsp;<span style="color:#00897b; font-weight: 600;">${occupancyText}</span><br/>';
    typeTooltip += '<br/><link-act name="smart_sensor">Smart sensor details</link-act><br/></div>';
} else if (deviceType == "energy-meter") {
    typeTooltip = '<br/><span style="font-weight: 500; color:#666;">Energy consumption:</span>&nbsp;<span style="color:#673ab7; font-weight: 600;">${energy:4} kWh</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">Power consumption:</span>&nbsp;<span style="color:rgb(30, 136, 229); font-weight: 600;">${power:0} W</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">Current (Amperage):</span>&nbsp;<span style="color:#f44336; font-weight: 600;">${amperage:2} A</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">Voltage:</span>&nbsp;<span style="color:#2196f3; font-weight: 600;">${voltage:2} V</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">Frequency:</span>&nbsp;<span style="color:rgb(104, 159, 56); font-weight: 600;">${frequency:2} Hz</span><br/>';
    typeTooltip += '<br/><link-act name="energy_meter">Energy meter details</link-act><br/></div>';
} else if (deviceType == "water-meter") {
    typeTooltip = '<br/><span style="font-weight: 500; color:#666;">Water consumption:</span>&nbsp;<span style="color:#01579b; font-weight: 600;">${water:3} gal</span><br/>';
    typeTooltip += '<br/><link-act name="water_meter">Water meter details</link-act><br/></div>';
} else if (deviceType == "hvac") {
    typeTooltip = '<br/><span style="font-weight: 500; color:#666;">State:</span>&nbsp;<span style="color:${enabledTextColor}; font-weight: 600;">${enabledText}</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">Target temperature:</span>&nbsp;<span style="color:#4caf50; font-weight: 600;">${targetTemperature:1} °C</span><br/>';
    typeTooltip += '<span style="font-weight: 500; color:#666;">Air flow:</span>&nbsp;<span style="color:#2196f3; font-weight: 600;">${airFlow:1} cfm</span><br/>';
    typeTooltip += '<br/><link-act name="hvac">HVAC details</link-act><br/></div>';
}

return tooltip + typeTooltip;

