    color: #fff;
}
.card .lower-bar {
    height: 30%;
    padding-left: 16px;
    display: flex;
    flex-direction: row;
    align-items: center;
}

.card .right-top-bar {
    display: none;
    position: absolute;
    top: 10px;
    right: 10px;
    display: flex;
    flex-direction: row;
    align-items: center;
}

.card .right-top-bar .icon-container {
    display: none;
    box-shadow: 0px 0px 4px 2px rgba(0,0,0,.26);
    cursor: pointer;
