settings.cardCss:
.card {
   width: 100%;
   height: 100%;
   box-sizing: border-box;
}

.card .content {
   padding: 20px;
   display: flex;
   flex-direction: row;
   align-items: center;
   height: 100%;
   box-sizing: border-box;
}

.card .content .column {
   display: flex;
   flex-direction: column;    
   height: 100%;
}

.card h1 {
    text-transform: uppercase;
    color: #777;
    font-size: 18px;
    font-weight: bold;
    margin: 0;
    padding-bottom: 6px;
    line-height: 20px;
}

.card h2 {
    color: #ffffff;
    font-size: 16px;
    font-weight: bold;
    margin: 0;
    padding-bottom: 10px;
    line-height: 20px;
}

.card h3 {
    color: #ffffff;
    font-size: 14px;
    font-weight: bold;
    margin: 0;
    padding-bottom: 6px;
    padding-top: 14px;
    line-height: 18px;
}

.card .value {
    font-size: 14px;
    font-weight: 300;
}

.card .description {
    font-size: 20px;
    color: #fffffff;
}

.card i.fa {
    font-size: 20px;
    padding-right: 10px;
}

